(function () {
  'use strict'

  /* ------------------------------ Main code ------------------------------ */
})()

router.post(
  '/api/$ROUTE$',
  async (req: express.Request, res: express.Response) => {
    const token: string = getAuthToken(req)
    if (token) {
      res.json(await apiRouteHandler.$ROUTE$(token))
    } else {
      const errorCode = 1003
      res.json({
        code: errorCode,
        error: errorCodes[errorCode]
      })
    }
  }
)
